css:
.ticket-print {
    font-family: 'Arial', sans-serif; /* Простий шрифт без засічок */
    font-size: 14px; /* Базовий розмір, може потребувати коригування */
    line-height: 1.4;
    color: #000;
    width: 280px; /* Орієнтовна ширина для 80мм стрічки, підберіть під ваш принтер */
    /* width: 200px; */ /* Орієнтовна ширина для 58мм стрічки */
    margin: 0;
    padding: 5px;
    text-align: center; /* Центрування за замовчуванням */
}
.ticket-print p, .ticket-print div {
    margin: 3px 0;
}
.ticket-print .org-name {
    font-weight: bold;
    font-size: 1.1em;
}
.ticket-print .office-name {
    font-size: 1em;
}
 .ticket-print .address {
    font-size: 0.9em;
 }
.ticket-print .ticket-label {
    margin-top: 10px;
    font-size: 1.1em;
}
.ticket-print .ticket-number {
    font-size: 3.5em; /* Великий номер талону */
    font-weight: bold;
    line-height: 1.1;
    margin: 5px 0;
    /* Виділяємо тільки цифрову частину */
    color: #000;  /* Можна додати колір, але на термопринтері буде чорним */
}
.ticket-print .service-name {
    font-weight: bold;
    margin-top: 10px;
    font-size: 1.1em;
}
.ticket-print .datetime {
    margin-top: 10px;
    font-size: 0.9em;
}
.ticket-print .footer-text {
    margin-top: 15px;
    font-size: 0.9em;
    border-top: 1px dashed #ccc;
    padding-top: 5px;
}
 .ticket-print hr {
    border: none;
    border-top: 1px dashed #ccc;
    margin: 8px 0;
 }
 .ticket-print .info {
     font-size: 0.9em;
     margin-top: 10px;
 }

html:
{# --- Jinja Логіка для отримання даних --- #}
{%- set office = frappe.get_doc("QMS Office", doc.office) -%}
{%- set service = frappe.get_doc("QMS Service", doc.service) -%}
{%- set org_name = frappe.db.get_value("QMS Organization", office.organization, "organization_name") if office.organization else "" -%}

{# --- Виділяємо номер послідовності з імені/номера талону --- #}
{%- set display_number = doc.ticket_number.split('-')[-1] if doc.ticket_number and '-' in doc.ticket_number else doc.ticket_number -%}

<div class="ticket-print">
    {# --- Інформація про організацію та офіс --- #}
    {% if org_name %}
    <div class="org-name">{{ org_name }}</div>
    {% endif %}
    <div class="office-name">{{ office.office_name or doc.office }}</div>
     {% if office.address %}
    <div class="address">{{ office.address }}</div>
     {% endif %}

    <hr>

    {# --- Номер талону --- #}
    <div class="ticket-label">ВАШ НОМЕР:</div>
    <div class="ticket-number">{{ display_number or "???" }}</div>

    {# --- Назва Послуги --- #}
    <div class="service-name">{{ service.service_name or doc.service }}</div>

    {# --- Дата та час видачі --- #}
    <div class="datetime">
        Дата: {{ frappe.utils.format_date(doc.issue_time or doc.creation, "dd.MM.yyyy") }}<br>
        Час: {{ frappe.utils.format_time(doc.issue_time or doc.creation, "HH:mm:ss") }}
    </div>

    {# --- Додаткова інформація (опціонально) --- #}
    
    {% set waiting_count = frappe.db.count("QMS Ticket", {"office": doc.office, "status": "Waiting", "creation": ["<", doc.creation]}) %}
    {% if waiting_count is defined and waiting_count >= 0 %}
    <div class="info">Осіб у черзі перед вами: {{ waiting_count }}</div>
    {% endif %}
    

    {# --- QR Код (потребує генерації та даних) --- #}
    {#
    <div>
        <img src="{{ frappe.utils.get_qrcode('URL_ДЛЯ_ПЕРЕВІРКИ_СТАТУСУ_ТАЛОНУ' + doc.name) }}" width="100">
    </div>
    #}

    {# --- Текст у підвалі --- #}
    <div class="footer-text">
        Будь ласка, очікуйте на виклик.<br>
        Слідкуйте за інформацією на табло.
    </div>

</div>
